<?phpclass ModelVendorVendorGroup extends Model {	public function addUserGroup($data) {		$this->db->query("INSERT INTO " . DB_PREFIX . "user_group SET name = '" . $this->db->escape($data['name']) . "', permission = '" . (isset($data['permission']) ? $this->db->escape(serialize($data['permission'])) : '') . "'");			return $this->db->getLastId();	}	public function editUserGroup($user_group_id, $data) {		$this->db->query("UPDATE " . DB_PREFIX . "user_group SET name = '" . $this->db->escape($data['name']) . "', permission = '" . (isset($data['permission']) ? $this->db->escape(serialize($data['permission'])) : '') . "' WHERE user_group_id = '" . (int)$user_group_id . "'");	}	public function deleteUserGroup($user_group_id) {		$this->db->query("DELETE FROM " . DB_PREFIX . "user_group WHERE user_group_id = '" . (int)$user_group_id . "'");	}	public function getUserGroup($user_group_id) {		$query = $this->db->query("SELECT DISTINCT * FROM " . DB_PREFIX . "user_group WHERE user_group_id = '" . (int)$user_group_id . "'");		$user_group = array(			'name'       => $query->row['name'],			'permission' => unserialize($query->row['permission'])		);		return $user_group;	}	public function getExcelStoreMapping($user_id) {		return $this->db->query("SELECT * FROM " . DB_PREFIX . "excel_store_mapping WHERE vendor_id=".$user_id)->rows;	}	public function deleteExcelStoreMap($id) {		$this->db->query("DELETE FROM " . DB_PREFIX . "excel_store_mapping WHERE id = '" . (int)$id."'");	}		public function getUserGroups($data = array()) {		$sql = "SELECT * FROM " . DB_PREFIX . "user_group";		                               //filter vendor groups                 $sql .= ' WHERE user_group_id IN ('.$this->config->get('config_vendor_group_ids').')';                		if (isset($data['filter_user_group']) && !is_null($data['filter_user_group']) && $data['filter_user_group'] != '*') {				$sql .= " AND name LIKE '" . $this->db->escape($data['filter_user_group']) . "%'";		}						$sql .= " ORDER BY name";		if (isset($data['order']) && ($data['order'] == 'DESC')) {			$sql .= " DESC";		} else {			$sql .= " ASC";		}		if (isset($data['start']) || isset($data['limit'])) {			if ($data['start'] < 0) {				$data['start'] = 0;			}			if ($data['limit'] < 1) {				$data['limit'] = 20;			}			$sql .= " LIMIT " . (int)$data['start'] . "," . (int)$data['limit'];		}		$query = $this->db->query($sql);		return $query->rows;	}	public function getTotalUserGroups() {                            $sql = "SELECT COUNT(*) AS total FROM " . DB_PREFIX . "user_group";                                 //filter vendor groups                 $sql .= ' WHERE user_group_id IN ('.$this->config->get('config_vendor_group_ids').')';                		$query = $this->db->query($sql);		return $query->row['total'];	}	public function addPermission($user_group_id, $type, $path) {		$user_group_query = $this->db->query("SELECT DISTINCT * FROM " . DB_PREFIX . "user_group WHERE user_group_id = '" . (int)$user_group_id . "'");		if ($user_group_query->num_rows) {			$data = unserialize($user_group_query->row['permission']);			$data[$type][] = $path;			$this->db->query("UPDATE " . DB_PREFIX . "user_group SET permission = '" . $this->db->escape(serialize($data)) . "' WHERE user_group_id = '" . (int)$user_group_id . "'");		}	}	public function removePermission($user_group_id, $type, $path) {		$user_group_query = $this->db->query("SELECT DISTINCT * FROM " . DB_PREFIX . "user_group WHERE user_group_id = '" . (int)$user_group_id . "'");		if ($user_group_query->num_rows) {			$data = unserialize($user_group_query->row['permission']);			$data[$type] = array_diff($data[$type], array($path));			$this->db->query("UPDATE " . DB_PREFIX . "user_group SET permission = '" . $this->db->escape(serialize($data)) . "' WHERE user_group_id = '" . (int)$user_group_id . "'");		}	}}