<?phpclass ModelShippingPickup extends Model {    function getQuote($cost ,$name) {        $this->load->language('shipping/pickup');        $status = true;        $method_data = array();        if ($status) {            $quote_data = array();            $quote_data['pickup'] = array(                'code' => 'pickup.pickup',                'title' => $this->language->get('text_description').'-'.$name,                'title_with_store' => $this->language->get('text_description').'-'.$name,                'cost' => 0.00,                'actual_cost'         => 0.00,                'tax_class_id' => 0,                'text' => $this->currency->format(0.00)            );            $method_data = array(                'code' => 'pickup',                'title' => $this->language->get('text_title'),                'quote' => $quote_data,                'sort_order' => $this->config->get('pickup_sort_order'),                'error' => false            );        }                return $method_data;    }    function getApiQuote($cost = '' ,$name = '',$subtotal,$total) {        $this->load->language('shipping/pickup');        $status = true;        $method_data = array();        if ($status) {            $quote_data = array();            $quote_data['pickup'] = array(                'code' => 'pickup.pickup',                'title' => $this->language->get('text_description'),//.'-'.$name,                'title_with_store' => $this->language->get('text_description').'-'.$name,                'actual_cost'         => 0.00,                'cost' => 0.00,                'tax_class_id' => 0,                'text' => $this->currency->format(0.00)            );            $method_data = array(                'code' => 'pickup',                'title' => $this->language->get('text_title'),                'quote' => $quote_data,                'sort_order' => $this->config->get('pickup_sort_order'),                'error' => false            );        }                return $method_data;    }    public function getShippingCharegApiQuote($cost = '' ,$name = '',$subtotal,$total,$shipping_address_id = '',$store_id = false) {        $this->load->language('shipping/pickup');        $status = true;        $method_data = array();        if ($status) {            $quote_data = array();            $quote_data['pickup'] = array(                'code' => 'pickup.pickup',                'title' => $this->language->get('text_description'),//.'-'.$name,                'title_with_store' => $this->language->get('text_description').'-'.$name,                'actual_cost'         => (int)0.00,                'cost' => (int)0.00,                'tax_class_id' => 0,                'select_delivery_text' => 'Select Delivery Date & Time',                'text' => $this->currency->format(0.00)            );            $method_data = array(                'code' => 'pickup',                'title' => $this->language->get('text_title'),                'quote' => $quote_data,                'sort_order' => $this->config->get('pickup_sort_order'),                'error' => false            );        }                return $method_data;    }    }